import React, { useState, useEffect } from "react";
import { Accordion, Button, ProgressBar } from 'react-bootstrap'
import { useAuth } from '../context/AuthContext'
import "./Poll.css";

function Poll(){
    const {currentUser} = useAuth();
    const [data, setData] = useState();
    const [voteData, setVoteData] = useState();
    const [users, setUsers] = useState([]);
    const [totalVotes, setTotalVotes] = useState(0);

    const url = "http://localhost:5001/pollItem?pollId=SITO_CATTANEA";
    
    useEffect(() => {
        fetch(url)
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            setVoteData(data["Item"]["options"]);
            setUsers(data["Item"]["usersPoll"]);
            setData(data);
            let sum = 0;
            data["Item"]["options"].forEach(function (obj){
                sum += obj.voted;
            });
            setTotalVotes(sum);
        });
    }, []);

    const submitVote = (e) => {
        console.log('event.currentTarget.dataset.id', e.currentTarget.dataset.id);
        if(!users.includes(currentUser.email)){
            users.push(currentUser.email);
            const voteSelected = e.currentTarget.dataset.id;
            const voteCurrent = voteData[voteSelected].voted;
            voteData[voteSelected].voted = voteCurrent + 1;
            setTotalVotes(totalVotes + 1);
            data["Item"]["options"] = voteData;
            data["Item"]["usersPoll"] = users;
            const options = {
                method: "POST",
                body: JSON.stringify(data),
                headers: { "Content-Type": "application/json"},
            };
            fetch(url, options)
                .then((res) => console.log(res));
        }
    };

    let pollOptions;

    if (voteData) {
        pollOptions = voteData.map((item) => {
            return (
                <li key={item.value}>
                    <Button variant="dark" onClick={submitVote} data-id={item.id}>{item.value}</Button>
                </li>
            );
        });
    }

    let pollProgressbar;

    if(users.includes(currentUser.email)){
        pollProgressbar = voteData.map((item) => {
            return (
                <li>
                    <ProgressBar variant="success" now={Number.parseFloat((item.voted*100/totalVotes)).toFixed(2)} label={item.value + ' - ' + Number.parseFloat((item.voted*100/totalVotes)).toFixed(2) + '%'}/>
                </li>
            );
        });
        return (
            <Accordion>
                <Accordion.Item eventKey={data["Item"]["pollID"]}>
                    <Accordion.Header>{data["Item"]["pollID"]}</Accordion.Header>
                    <Accordion.Body>
                        <div className="poll">
                        <h1>Which option do you like the best?</h1>
                        <ul className={"options"}>
                            {pollProgressbar}
                        </ul>
                        <p>Total Votes: {totalVotes}</p>
                        </div>
                    </Accordion.Body>
                </Accordion.Item>
            </Accordion>
          );
    } else {
        return (
            <div className="poll">
              <h1>Which option do you like the best?</h1>
              <ul className={"options"}>
                {pollOptions}
              </ul>
              <p>Total Votes: {totalVotes}</p>
            </div>
          );
    }

}
export default Poll;
